class PostRepository extends EntityRepository
{
    public function findLatest($limit)
    {
        $qb = $this->createQueryBuilder('a');
        $qb 
           ->orderBy('a.id', 'DESC')
           ->setMaxResults($limit) // pour crÃ©er la limite
        ;
                       
        return $qb->getQuery()->getResult();
    }
