    public function findByCategory($category)
    {
        $query = $this->createQueryBuilder('p')
                      ->leftJoin('p.categories', 'c')
                      ->where('c.id = :c')
                      ->setParameter('c', $category)
                      ->getQuery()
                      ->getResult();
          
        return $query;
    }
    
